	(252,'_transient_a1a994fe14eed12a6d93ddc6b4df76aa','O:8:\"stdClass\":21:{s:4:\"name\";s:21:\"WordPress Beta Tester\";s:4:\"slug\";s:21:\"wordpress-beta-tester\";s:7:\"version\";s:3:\"1.0\";s:6:\"author\";s:52:\"<a href=\"http://blog.ftwr.co.uk/\">Peter Westwood</a>\";s:14:\"author_profile\";s:30:\"//profiles.wordpress.org/westi\";s:12:\"contributors\";a:1:{s:5:\"westi\";s:30:\"//profiles.wordpress.org/westi\";}s:8:\"requires\";s:5:\"3.0.5\";s:6:\"tested\";s:5:\"4.2.5\";s:13:\"compatibility\";a:34:{s:5:\"2.8.5\";a:2:{s:3:\"0.6\";a:3:{i:0;i:100;i:1;i:2;i:2;i:2;}s:3:\"0.7\";a:3:{i:0;i:100;i:1;i:2;i:2;i:2;}}s:5:\"2.8.6\";a:2:{s:3:\"0.7\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}s:3:\"0.8\";a:3:{i:0;i:100;i:1;i:9;i:2;i:9;}}s:3:\"2.9\";a:3:{s:3:\"0.6\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}s:3:\"0.8\";a:3:{i:0;i:100;i:1;i:2;i:2;i:2;}s:4:\"0.81\";a:3:{i:0;i:100;i:1;i:8;i:2;i:8;}}s:5:\"2.9.1\";a:1:{s:4:\"0.81\";a:3:{i:0;i:100;i:1;i:2;i:2;i:2;}}s:5:\"2.9.2\";a:5:{s:4:\"0.81\";a:3:{i:0;i:100;i:1;i:7;i:2;i:7;}s:4:\"0.90\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}s:4:\"0.91\";a:3:{i:0;i:100;i:1;i:3;i:2;i:3;}s:4:\"0.92\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}s:4:\"0.93\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:3:\"3.0\";a:2:{s:4:\"0.92\";a:3:{i:0;i:100;i:1;i:4;i:2;i:4;}s:4:\"0.93\";a:3:{i:0;i:100;i:1;i:3;i:2;i:3;}}s:5:\"3.0.1\";a:2:{s:4:\"0.92\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}s:4:\"0.93\";a:3:{i:0;i:100;i:1;i:4;i:2;i:4;}}s:5:\"3.0.2\";a:1:{s:4:\"0.93\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:5:\"3.0.3\";a:1:{s:4:\"0.93\";a:3:{i:0;i:0;i:1;i:1;i:2;i:0;}}s:3:\"3.1\";a:1:{s:4:\"0.93\";a:3:{i:0;i:78;i:1;i:9;i:2;i:7;}}s:5:\"3.1.1\";a:1:{s:4:\"0.93\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:5:\"3.1.2\";a:2:{s:4:\"0.93\";a:3:{i:0;i:100;i:1;i:3;i:2;i:3;}s:4:\"0.95\";a:3:{i:0;i:0;i:1;i:1;i:2;i:0;}}s:5:\"3.1.3\";a:2:{s:4:\"0.94\";a:3:{i:0;i:100;i:1;i:2;i:2;i:2;}s:4:\"0.95\";a:3:{i:0;i:100;i:1;i:4;i:2;i:4;}}s:3:\"3.2\";a:1:{s:4:\"0.95\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:5:\"3.2.1\";a:1:{s:4:\"0.95\";a:3:{i:0;i:11;i:1;i:19;i:2;i:2;}}